"use client";
import React from "react";
import "./dept.css";
import Image from "next/image";
import icon from "../../images/icon.png";
import Header from "@/components/navbar";
import Footer from "@/components/footer";

export default function Page() {
  const handleCallClick = () => {
    const phoneNumber = "+265 997 138 340"; // Replace with the actual phone number
    window.location.href = "tel:" + phoneNumber;
  };

  const availablePaymentMethods = [
    {
      id: 1,
      name: "Airtel Money",
      logo: "data:image/png;base64,",
    },
    {
      id: 2,
      name: "Mpamba",
      logo: "data:image/png;base64,",
    },
    {
      id: 3,
      name: "Cash",
      logo: "https://e7.pngegg.com/pngimages/178/28/png-clipart-computer-icons-money-bag-bank-cash-angle-hand-thumbnail.png",
    },
    {
      id: 4,
      name: "Banks",
      logo: "https://static.vecteezy.com/system/resources/previews/013/948/616/non_2x/bank-icon-logo-design-vector.jpg",
    },
    {
      id: 5,
      name: "Medical Schemes",
      logo: "data:image/png;base64,",
    },
  ];

  return (
    <>
      <Header />
      <div className="divyadept">
        <div className="deptinfo">
          <div className="tiyikelogo">
            <Image
              src={icon}
              alt="zilogo"
              width={200}
              height={20}
              className="zitobe"
            />
          </div>
          <div className="infoija">
            <h1 className="deptTitle">Department of Finance</h1>
            <p className="deptdis">
              The Finance Department of the hospital is the nerve center of
              financial management, ensuring the efficient allocation of
              resources to support optimal patient care. Responsible for
              budgeting, expenditure tracking, and financial forecasting, it
              plays a pivotal role in maintaining the institution&apos;s fiscal
              health. Additionally, the department manages billing processes,
              liaises with insurance companies, and maintains transparent
              financial records to uphold accountability and compliance. Through
              its dedication to financial stewardship and operational
              excellence, the Finance Department enables the hospital to fulfill
              its mission of delivering high-quality healthcare while ensuring
              financial sustainability.
            </p>
            <div className="mt-10 flex items-center gap-x-4">
              <h4 className="flex-none text-sm font-semibold leading-6 text-indigo-600">
                Available Payment Methods
              </h4>
              <div className="h-px flex-auto bg-gray-100" />
            </div>
            <ul
              role="list"
              className="mt-8 grid grid-cols-1 gap-4 text-sm leading-6 text-gray-600 sm:grid-cols-2 sm:gap-6"
            >
              {availablePaymentMethods.map((method) => (
                <li key={method.id} className="flex gap-x-3 items-center">
                  <img
                    src={method.logo}
                    alt={method.name}
                    className="h-6 w-6"
                  />
                  <span>{method.name}</span>
                </li>
              ))}
            </ul>
            <br></br>
            <span className="deptgcpd">To contact the department call</span>
            <button onClick={handleCallClick}>
              <a>call now</a>
            </button>
          </div>
        </div>
        <div className="deptImg"></div>
      </div>
      <Footer />
    </>
  );
}
